/**
 * Script for day 1 in advent of code calendar
 * http://adventofcode.com/2017/day/1
 * 
 * Quick and dirty , just get the result.
 * 
 * To run this code, run the command node d1.js
 * 
 */

class Puzzle {

  constructor() {

    this.inputString = ""
    
    this.inputTest1 = "1122"
    this.expectTest1 = 3
    
    this.inputTest2 = "1111"
    this.expectTest2 = 4
    
    this.inputTest3 = "1234"
    this.expectTest3 = 0
    
    this.inputTest4 = "91212129"
    this.expectTest4 = 9

  }

  getAnswer() {
    let theAnswerIs_Attention_dot_dot_dot = this.theSolver(this.inputString)
    return `The answer is ... ${theAnswerIs_Attention_dot_dot_dot}`
  }

  theSolver( inputVal ) {
    let returnVal = 0

    for( var i = 0; i < inputVal.length; i++ ){
      
      let currentVal = inputVal[i]
      let nextVal = (i+1 < inputVal.length) ? inputVal[i+1] : inputVal[0]

      if( currentVal == nextVal) {
        returnVal += parseInt(currentVal)
      }

    }

    return returnVal
  }

  theChecker() {

    let test1 = this.theSolver(this.inputTest1)
    if( test1 === this.expectTest1 ) {
      console.log("> test 1 OK :)");
    }else{
      console.log(`> test 1 Not OK :(  => Got ${test1} , expecting ${this.expectTest1}`);
    }

    let test2 = this.theSolver(this.inputTest2)
    if( test2 === this.expectTest2 ) {
      console.log("> test 2 OK :)");
    }else{
      console.log(`> test 2 Not OK :(  => Got ${test2} , expecting ${this.expectTest2}`);
    }
    
    let test3 = this.theSolver(this.inputTest3)
    if( test3 === this.expectTest3 ) {
      console.log("> test 3 OK :)");
    }else{
      console.log(`> test 3 Not OK :(  => Got ${test3} , expecting ${this.expectTest3}`);
    }

    let test4 = this.theSolver(this.inputTest4)
    if( test4 === this.expectTest4 ) {
      console.log("> test 4 OK :)");
    }else{
      console.log(`> test 4 Not OK :(  => Got ${test4} , expecting ${this.expectTest4}`);
    }


  }

}

let puzzle = new Puzzle();
